    Eigen::VectorXd Yvec0(4);
    Yvec0 << H00, V0, W0, H00der;

    get_alpha_beta_funcs_ders_and_shear_source(l, Omega, xi, theta, n, brel, gamma_0, lambda, nu, feta, feta_der, feta_der_2,
    H00, V0, W0, H00der, y , dydx);


